#include <stdio.h>
#include <string.h>

int main(){

    char string[1001];
    int tamanho,i,j,numeros=5;
    long long produtomaximo=0,produto;
    char maximonumeros[6];

    strcpy(string,"");

    tamanho = strlen(string);

    for(i=0;i<=tamanho-numeros;i++){
        produto=1;
        for(j=i;j<i+numeros;j++){
            produto *= string[j] - '0';
        }
        if(produto>produtomaximo){
            produtomaximo=produto;
            strncpy(maximonumeros, string+i, 5);
            maximonumeros[5] = '\0';
        }
    }
    printf("O conjunto de 5 numeros consecutivos que gera o maior produto eh %s.\n",maximonumeros);

    return 0;
}
